mapper.delete:
delete
from sys_object_map
where object_name = #{objectName}
  and object_id = #{objectId}
  and target_name = #{targetName}

mapper.select:
select target_id
from sys_object_map
where object_name = #{objectName}
  and object_id = #{objectId}
  and target_name = #{targetName}

mapper.delete:
delete
from sys_object_map
where target_name = #{targetName}
  and target_id = #{targetId}
  and object_name = #{objectName}

mapper.select:
select object_id
from sys_object_map
where target_name = #{targetName}
  and target_id = #{targetId}
  and object_name = #{objectName}
